"use strict";var container=document.getElementById("container"),displayData=function(n){var e=n.latitude,i=n.longitude;fetch("https://fcc-weather-api.glitch.me/api/current?lat="+e+"&lon="+i).then(function(n){return n.json()}).then(function(n){return putData(n)})};fetch("https://freegeoip.net/json/").then(function(n){return n.json()}).then(displayData);var putData=function(n){console.log(n);var e=Temperature(n.main.temp),i=WeatherIcon(n.weather[0].icon),t=Weathertype(n.weather[0].main.toLowerCase()),r=Info(n),a=WeatherTemplate(e,i,r,t);container.innerHTML=a},Temperature=function(n){return'\n    <div id="temp">\n      '+n+" Â°C\n    </div>\n  "},WeatherIcon=function(n){return"\n    <div id='image'>\n      <img src='"+n+"'>\n    </div>\n  "},Weathertype=function(n){return"\n    <div id='type'>\n      The weather is "+n+"\n    </div>\n  "},Info=function(n){var e=n.main,i=n.wind;return"<div id='info'>\n    <div id='wind'>\n      <div id='wind-title'>Wind</div>\n      <div id='wind-speed'>Speed:  "+i.speed+" <span class='unit'>km/hr</span></div>\n      <div id='wind-dir'>Direction: "+calculateWindDirection(i.deg)+"</div>\n      \n    </div>\n    <div id='humidity'>\n      Humidity: "+e.humidity+" %\n    </div>\n    <div id='pressure'>\n      Pressure: "+e.pressure+" <span class='unit'>mb</span>\n    </div>\n  </div>"},WeatherTemplate=function(){for(var n=arguments.length,e=Array(n),i=0;n>i;i++)e[i]=arguments[i];return e.join("")},calculateWindDirection=function(n){var e=["N","NNE","NE","ENE","E","ESE","SE","SSE","S","SSW","SW","WSW","W","WNW","NW","NNW","N"],i=Math.floor(n/22.5);return e[i]};